import React, { useState } from "react"
const Blog = ({ blog }) => {
  const [expand, setExpand] = useState(false)

  if (expand) {
    return (
      <div className="blog">
        {blog.title} {blog.author}{" "}
        <button onClick={() => setExpand(true)}></button>
      </div>
    )
  }
}

export default Blog
