import React, { useState } from "react"
const Blog = ({ blog }) => {
  const [expand, setExpand] = useState(false)

  return (
    <div className="blog">
      {blog.title}
      <button onClick={() => setExpand(!expand)}>
        {expand ? "hide" : "view"}
      </button>
      {expand && (
        <>
          <div>{blog.url}</div>
          <div>{blog.likes}</div>
          <div>{blog.author}</div>
        </>
      )}
    </div>
  )
}

export default Blog
